import * as Typegen from 'nexus-plugin-prisma/typegen'
import * as Prisma from '../../.prisma/client';

// Pagination type
type Pagination = {
  first?: boolean
  last?: boolean
  before?: boolean
  after?: boolean
}

// Prisma custom scalar names
type CustomScalars = 'No custom scalars are used in your Prisma Schema.'

// Prisma model type definitions
interface PrismaModels {
  Todo: Prisma.Todo
}

// Prisma input types metadata
interface NexusPrismaInputs {
  Query: {
    todos: {
      filtering: 'AND' | 'OR' | 'NOT' | 'id' | 'description'
      ordering: 'id' | 'description'
    }
  },
  Todo: {

  }
}

// Prisma output types metadata
interface NexusPrismaOutputs {
  Query: {
    todo: 'Todo'
    todos: 'Todo'
  },
  Mutation: {
    createOneTodo: 'Todo'
    updateOneTodo: 'Todo'
    updateManyTodo: 'BatchPayload'
    deleteOneTodo: 'Todo'
    deleteManyTodo: 'BatchPayload'
    upsertOneTodo: 'Todo'
  },
  Todo: {
    id: 'Int'
    description: 'String'
  }
}

// Helper to gather all methods relative to a model
interface NexusPrismaMethods {
  Todo: Typegen.NexusPrismaFields<'Todo'>
  Query: Typegen.NexusPrismaFields<'Query'>
  Mutation: Typegen.NexusPrismaFields<'Mutation'>
}

interface NexusPrismaGenTypes {
  inputs: NexusPrismaInputs
  outputs: NexusPrismaOutputs
  methods: NexusPrismaMethods
  models: PrismaModels
  pagination: Pagination
  scalars: CustomScalars
}

declare global {
  interface NexusPrismaGen extends NexusPrismaGenTypes {}

  type NexusPrisma<
    TypeName extends string,
    ModelOrCrud extends 'model' | 'crud'
  > = Typegen.GetNexusPrisma<TypeName, ModelOrCrud>;
}
  